(window.webpackJsonp=window.webpackJsonp||[]).push([[8],{282:function(t,v,_){t.exports=_.p+"assets/img/uestc.f6f2e64e.jpg"},293:function(t,v,_){"use strict";_.r(v);var s=_(14),e=Object(s.a)({},(function(){var t=this,v=t._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h2",{attrs:{id:"学校概况"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#学校概况"}},[t._v("#")]),t._v(" 学校概况")]),t._v(" "),v("p",[v("img",{attrs:{src:_(282),alt:"uestc"}})]),t._v(" "),v("p",[t._v("电子科技大学坐落于四川省成都市，原名成都电讯工程学院，是1956年在周恩来总理的亲自部署下，由交通大学（现上海交通大学、西安交通大学）、南京工学院（现东南大学）、华南工学院（现华南理工大学）的电讯工程有关专业合并创建而成。")]),t._v(" "),v("p",[t._v("学校1960年被中共中央列为全国重点高等学校，1961年被中共中央确定为七所国防工业院校之一，1988年更名为电子科技大学，1997年被确定为国家首批“211工程”建设的重点大学，2000年由原信息产业部主管划转为教育部主管，2001年进入国家“985工程”重点建设大学行列，2017年进入国家建设“世界一流大学”A类高校行列。2019年教育部和四川省签约共同推进我校世界一流大学建设。2022年入选国家第二轮“双一流”建设高校名单。经过60余年的建设，学校形成了从本科到硕士研究生、博士研究生等多层次、多类型的人才培养格局，成为一所完整覆盖整个电子信息类学科，以电子信息科学技术为核心，以工为主，理工渗透，理、工、管、文、医协调发展的多科性研究型大学，成长为国内电子信息领域高新技术的源头，创新人才的基地。")]),t._v(" "),v("p",[t._v("学校设有清水河、沙河、九里堤、永宁四个校区，校舍总建筑面积139万余平方米，拥有馆藏丰富的现代化数字图书馆和一批设施齐备的现代化体育场馆。校园四季树木葱茏、湖水碧波荡漾、建筑典雅厚重，是陶冶情操、读书治学的佳境。")]),t._v(" "),v("p",[t._v("学校坚持学生为中心、通专结合，以“价值塑造、启迪思想、唤起好奇、激发潜能、探究知识、个性发展”六位一体为培养理念，致力于培养具有家国情怀、全球素养、扎实基础、知识综合与集成创新能力，未来能引领学术前沿、科技与社会经济发展，堪当民族复兴大任的创新引领性人才。学校设有23个学院（部），65个本科专业，其中13个为国家级特色专业，32个专业入选国家级一流本科专业建设点，现有在读本、硕、博学生40000余人。本科生国内外深造率超过2/3，其中出国（境）深造率超过1/5。学生就业率一直保持在95%以上，超过半数的研究生赴国家重点单位就业。研究生在国际顶级学术期刊发表论文的数量显著增加，获得省部级及以上科技成果奖的人数不断增加。成电学子遍布海内外，以素质全面、专业知识扎实、能力强、后劲足等鲜明特点受到了社会各界和用人单位的普遍赞誉。")]),t._v(" "),v("p",[t._v("学校高度重视学生创新实践能力培养，支持和鼓励学生积极参与科技创新、文化艺术和社会实践活动。学生活跃在中国国际“互联网+”大学生创新创业大赛、“挑战杯”全国大学生课外学术科技作品竞赛、“挑战杯”全国大学生创业计划大赛、“创青春”全国大学生创业大赛、中国研究生创新实践系列大赛、全国大学生电子设计竞赛、全国大学生集成电路创新创业大赛、全国大学生数学建模竞赛、IEEEXtreme极限编程大赛、ACM-ICPC国际大学生程序设计竞赛、全国大学生机器人竞赛、全球大学生基因工程大赛、国际大专辩论赛、全国大学生艺术比赛等高水平赛场上，争金夺银，成绩斐然，在“中国高校创新人才培养暨学科竞赛评估”中名列前茅。")]),t._v(" "),v("p",[t._v("学校已建成国家精品在线开放课程等一批国家精品课程、精品教材，拥有国家大学生文化素质教育基地，以及国家级实验教学示范中心、虚拟仿真实验教学示范中心、工程实践教育中心、全国工程专业学位研究生联合培养示范基地等20余个国家级教育教学实践基地和示范中心，获得一批国家级教学成果奖。")]),t._v(" "),v("p",[t._v("学校大力实施学科提升战略，扎实推进理工深度融合，学科影响力持续提升。学校现有2个国家一级重点学科（所包括的6个二级学科均为国家重点学科）、2个国家重点（培育）学科。一级学科博士学位授权点19个，一级学科硕士学位授权点31个，博士专业学位授权点4个、硕士专业学位授权点12个。设有博士后流动站15个。在第四轮全国一级学科评估中，学校4个学科获评A类，其中电子科学与技术、信息与通信工程两个学科为A+，A+学科数并列西部高校第一。")]),t._v(" "),v("p",[t._v("学校大力实施服务国家重大需求战略，以“顶天、立地、树人”为科研工作定位，坚持“开放、聚焦、融合、奋斗”的发展理念，面向世界科技前沿、面向经济主战场、面向国家重大需求、面向人民生命健康，努力推进基础研究与应用研究并重的科技创新格局，不断提升集成攻关和原始创新能力。学校拥有国家级重点实验室4个，国家工程技术研究中心1个，国家地方联合工程实验室（研究中心）2个，共建国家工程实验室2个、国家级工业创新中心1个，首批国家专业化众创空间1个，省部级科研机构59个，国家自然科学基金委创新群体6个、教育部创新团队6个和国防科技创新团队3个。获批建设国家集成电路产教融合创新平台。“十一五”以来，学校科技成果获国家级奖励35项、部省级奖励365项。学校充分发挥学科、人才和科研优势，积极投身“一带一路”建设及粤港澳大湾区、长江经济带建设，在珠三角、长三角、成渝地区双城经济圈等设立研究机构，积极服务四川省产业转型升级，助力四川构建现代化产业体系。与成都市共同实施“一校一带”行动计划，建设高校成果转化产业带，携手打造中国“西部硅谷”。")]),t._v(" "),v("p",[t._v("学校大力实施人才优先发展战略，现有教职工3800余人，其中教师2500余人，教授660余人。截至目前，我校现有中国科学院院士、中国工程院院士等高层次人才300余人。")]),t._v(" "),v("p",[t._v("学校大力实施国际化发展战略，已与世界70多个国家和地区的200余所大学、科研机构、企业建立友好合作关系，同一批国外知名高校签署了学生交流及联合培养协议。在加拿大麦吉尔大学、美国威廉玛丽学院和英国格拉斯哥大学等建立了海外高端学术培育基地和教学培训基地。与英国牛津大学、美国加州大学系列等世界名校建立长期合作关系，通过境内学习环节和境外学习环节的有机结合构建了学校国际化人才培养体系。40%的本科学生在校期间有海外学习经历，学生长短期留学目的地覆盖近50个国家和地区。现有1000余名来自全球70多个国家和地区的长期留学生在校学习。建立了3个国家级国际联合研究中心，若干个国际联合科研平台。积极实施“一流伙伴计划”推进国际化人才培养，与格拉斯哥大学、麦吉尔大学、瑞典皇家理工学院等建立了本硕博层次的多个中外合作办学项目。")]),t._v(" "),v("p",[t._v("电子科技大学以“求实求真、大气大为”为校训，以人才培养为根本，以服务国防建设和国家、地方经济建设为己任，加强基础前沿交叉研究，开拓进取，锐意创新，为早日建成中国特色世界一流大学而努力奋斗！")]),t._v(" "),v("p",[v("s",[t._v("学校的官话罢了")])])])}),[],!1,null,null,null);v.default=e.exports}}]);